$(document).ready(function() {
	var deheight = $(window).height();
	$("body").height(deheight);
	$(".homezzc").height(deheight + 60);
	pageinit(posturl);
	function pageinit(c) {
		$.ajax({
			url: c + "/api/IndexConfig",
			type: "post",
			async: false,
			success: function(e) {
				console.log(e);
				if(e.code == 200) {
					var d = Math.random();
					$(".abtn1").attr("obj", "floor");
					$(".abtn2").attr("obj", "sell");
					$(".abtn3").attr("obj", "add");
					$(".abtn4").attr("obj", "result");
					if(e.floor.open == 1) {
						$(".abtn1").attr("ope", e.floor.open)
					} else {
						$(".abtn1").attr("msg", e.floor.msg);
						$(".abtn1").attr("ope", e.floor.open)
					}
					if(e.sell.open == 1) {
						$(".abtn2").attr("ope", e.sell.open)
					} else {
						$(".abtn2").attr("msg", e.sell.msg);
						$(".abtn2").attr("ope", e.sell.open)
					}
					if(e.add.open == 1) {
						$(".abtn3").attr("ope", e.add.open)
					} else {
						$(".abtn3").attr("msg", e.add.msg);
						$(".abtn3").attr("ope", e.add.open)
					}
					if(e.result.open == 1) {
						$(".abtn4").attr("ope", e.result.open)
					} else {
						$(".abtn4").attr("msg", e.result.msg);
						$(".abtn4").attr("ope", e.result.open)
					}
				} else {
					$.alert(e.msg, "提示", function() {})
				}
			},
			error: function() {
				$(".pagepre .btn").addClass("btnok");
				$.alert("请检查您的网络，稍后再试！", "提示", function() {})
			}
		})
	}
	$(".abtn").click(function() {
		var d = $(this).attr("msg");
		var e = $(this).attr("ope");
		var c = $(this).attr("obj");
		$.ajax({
			url: posturl + "/api/IndexConfig",
			type: "post",
			async: false,
			success: function(j) {
				console.log(j);
				if(j.code == 200) {
					var g = Math.random();
					if(c == "floor") {
						if(j.floor.open == 1) {
							window.location.href = "info.html?v=" + g
						} else {
							$.alert(j.floor.msg, "售房派")
						}
					} else {
						if(c == "sell") {
							if(j.sell.open == 1) {
								window.location.href = "publicity.html?v=" + g
							} else {
								$.alert(j.sell.msg, "售房派")
							}
						} else {
							if(c == "add") {
								if(j.add.open == 1) {
									var f = getlocalStorage("phone");
									var g = Math.random();
									if(hasdata(f)) {
										var i = getlocalStorage("signtime");
										if(hasdata(i)) {
											var h = (new Date()).valueOf();
											if(h - i > 1000 * 60 * 60 * 6) {
												console.log("登录超时");
												window.location.href = "login.html?v=" + g
											} else {
												$.ajax({
													url: posturl + "/api/loginDirect",
													type: "post",
													data: {
														loginSign: f
													},
													async: false,
													success: function(k) {
														console.log(k);
														if(k.code == 200) {
															setlocalStorage("loginSignlocation", 1);
															if(k.status == "不跳") {
																window.location.href = "index.html?url=loginSign&v=" + g
															} else {
																window.location.href = "showdata.html?url=loginSign&v=" + g
															}
														} else {
															setlocalStorage("loginSignlocation", 2);
															window.location.href = "login.html?v=" + g
														}
													},
													error: function() {
														setlocalStorage("loginSignlocation", 2);
														window.location.href = "login.html?v=" + g
													}
												})
											}
										} else {
											setlocalStorage("loginSignlocation", 2);
											window.location.href = "login.html?v=" + g
										}
									} else {
										setlocalStorage("loginSignlocation", 2);
										window.location.href = "login.html?v=" + g
									}
								} else {
									$.alert(j.add.msg, "售房派")
								}
							} else {
								if(c == "result") {
									if(j.result.open == 1) {
										window.location.href = "shaking.html?v=" + g
									} else {
										$.alert(j.result.msg, "售房派")
									}
								}
							}
						}
					}
				} else {
					$.alert(j.msg, "提示", function() {})
				}
			},
			error: function() {
				$.alert("服务器忙，请稍后再试！", "提示", function() {})
			}
		})
	})
});